class Tree_widget(QtWidgets.QTreeWidget):
    """
    on override QTreeWidget pour y ajouter des methode servant Ã 
    l'ajout de figures et fichiers. Cette class reprÃ©sente l'arbre
    "pysique", c'est l'objet qui gÃ©re l'affichage, len events etc

    En plus de cette arbre, un arbre "logique" est crÃ©Ã©e, il comportera
    la mÃªme structure que l'arbre phyqiue dont il dÃ©pends. Il servira a
    faciliter les calcules pour l'insersion de nouvelles figures.
    """

    def __init__(self, parent=None):
        super().__init__(parent)
        self.items = []

    """---------------------------------------------------------------------------------"""

    def add_data(self, type, name):
        """
        On ajoute la nouvelle data au tree

        :param type: type exp (cccv, cv, ...)
        :param name: nom du fichier de donnÃ©e
        :return: None
        """
        self.items.append(self.Conteneur_data(name))

        # on rÃ©cupÃ©re le nom et le type d'expÃ©rience pour crÃ©er un nouvel item
        item = QTreeWidgetItem([name, type])

        # c'est toujours un top level item, une experience ne dÃ©pends de rien
        self.addTopLevelItem(item)

        # on place l'item comme le focus
        self.setCurrentItem(item)

    """---------------------------------------------------------------------------------"""

    def add_figure(self, figure, current_data_name):
        """
        on ajoute la figure au tree en fonction de _created_from de la figure

        :param figure: objet Figure
        :param current_data_name: nom de la data auquel appartient la figure, le premier parent
        :return: None
        """
        for i, item in enumerate(self.items):
            if item.name == current_data_name:

                # la figure ne dÃ©pends pas d'une autre, on l'ajoute juste
                if figure.created_from is None:
                    # la figure n'a pas Ã©tait crÃ©Ã©e Ã  partir d'une autre

                    # on crÃ©Ã©er un nouvel objet Conteneur
                    new_item = self.Conteneur_figure(figure.name)

                    # on l'ajoute Ã  l'objet Conteneur_data correspondant Ã  current_data_name
                    item.append(new_item)

                    # on crÃ©Ã©er un nouvel objet QTreeWidgetItem
                    item = QTreeWidgetItem([figure.name, "figure"])

                    # on ajoute l'item Ã  la place correspondante
                    self.topLevelItem(i).addChild(item)

                    # on place le focus sur le dernier ajout
                    self.setCurrentItem(item)
                else:
                    # on crÃ©er un array qui liste les "origines" de la figure en parcourant rÃ©cursivement
                    # created_from de la figure est de ses parents
                    array_created_from = []
                    self.get_array_created_from(figure, array_created_from)

                    # on crÃ©er un nouvel item
                    item = QTreeWidgetItem([figure.name, "figure"])

                    # on crÃ©er obj qui correspond Ã  l'objet qui va parcourir l'arbre logique
                    obj = self.items[i]

                    # _item lui parcous l'arbre "physique"
                    _item = self.topLevelItem(i)
                    for i, name in enumerate(array_created_from):
                        # ou parcours l'arbre en utilisant les noms obtenus par get_array_created_from
                        obj, index = obj.get_child(name)
                        _item = _item.child(index)

                    # on est au bonne endroit de l'arbre logique et physique pour ajouter litem
                    # arbre logique
                    new_item = self.Conteneur_figure(figure.name)
                    obj.append(new_item)

                    # arbre pysique
                    _item.addChild(item)
                break

    """---------------------------------------------------------------------------------"""

    def get_array_created_from(self, figure, array_res):
        """
        crÃ©er un array contenant le nom des figure ayant servis Ã  la crÃ©ation de la figure passÃ©
        en paramÃ©tre. On parcours rÃ©cursivement les parent de la figure.

        :param figure: figure sur laquel la recherche de parent est effectuÃ©
        :param array_res: array dans lequel on stock les noms
        :return: array contenant les toutes les figure .created_from de figure
                 si figure Ã  Ã©tÃ© crÃ©Ã©e Ã  aprtir de f2, f2 Ã  partir de f3 et f3 Ã  partir de f4 :
                 :return [f4.name, f3.name, f2.name]
        """

        if figure.created_from is not None:
            self.get_array_created_from(figure.created_from, array_res)
            array_res.append(figure.created_from.name)

    """---------------------------------------------------------------------------------"""

    def info(self):
        """
        affiche de maniÃ©re textuel les informations de l'arbre logique

        :return: None
        """
        for conteneur in self.items:
            conteneur.get_name(0)

    """---------------------------------------------------------------------------------"""

    class Conteneur(ABC):
        """
        class abstraite de qui vont hÃ©riter Conteneur_data et Conteneur_figure
        elle fournis les methode pour aider le parcours de l'arbre logique
        """

        def __init__(self, name):
            self.name = name

        @abstractmethod
        def get_child(self, name):
            """
            return l'index et la figure correspondant au nom donnÃ©e en
            paramÃ¨tre, raise ValueError si elle n'est pas prÃ©sente dans
            le conteneur

            :param name: non du chiel que l'on souhaite avoir
            :return: Figure, index

            :exception ValueError
            """
            pass

        @abstractmethod
        def get_name(self, nb_tab):
            """
            Methode utilisÃ© pour l'affichage text de l'arbre logique, nb_tab
            est incrÃ©mentÃ© Ã  chaque fois que l'on passe Ã  l'affichage d'un child

            :param nb_tab: nombre tabulation pour l'affichage
            :return: None
            """
            pass

        @abstractmethod
        def append(self, item):
            """
            en fonction de la class fille l'array du conteneur ne porte pas ne mÃªme nom,
            on utilise cette methode pour append un item au conteneur

            :param item: Conteneur_data, Conteneur_figure
            :return: None
            """
            pass

    class Conteneur_data(Conteneur):
        def __init__(self, name):
            super().__init__(name)

            # objet de la class Conteneur_figure
            self.figures_child = []

        def get_child(self, name):
            for i, figure in enumerate(self.figures_child):
                if figure.name == name:
                    return figure, i
            raise ValueError

        def get_name(self, nb_tab):
            str = ""
            for i in range(nb_tab):
                str += "\t"
            str += self.name
            print(str)
            for item in self.figures_child:
                item.get_name(nb_tab + 1)

        def append(self, item):
            self.figures_child.append(item)

    class Conteneur_figure(Conteneur):
        def __init__(self, name):
            super().__init__(name)

            # objet de la class Conteneur_figure
            self.figures_child = []

        def get_child(self, name):
            for i, figure in enumerate(self.figures_child):
                if figure.name == name:
                    return figure, i
            raise ValueError

        def get_name(self, nb_tab):
            str = ""
            for i in range(nb_tab):
                str += "\t"
            str += self.name
            print(str)
            for item in self.figures_child:
                item.get_name(nb_tab + 1)

        def append(self, item):
            self.figures_child.append(item)
